  <script type="text/javascript">
	(function($) {
	  "use strict";
		var locations = [
		['<div class="infobox"><h3 class="title">ARV Engenharia</h3><span>Condomínio Atlantic Tower</span><br/><span>Av. Djalma Batista, 1719 • Sala 402-B</span><br>+55 92 0000 0000</p></div></div></div>', -3.100450, -60.024351, 2]
		];		
	
		var map = new google.maps.Map(document.getElementById('map'), {
		  zoom: 17,
			scrollwheel: false,
			navigationControl: true,
			mapTypeControl: false,
			scaleControl: false,
			draggable: true,
			styles: [ { "stylers": [ { "hue": "#000" },  {saturation: -100},
                {gamma: 1.50} ] } ],
			center: new google.maps.LatLng(-3.100450, -60.024351),
		  mapTypeId: google.maps.MapTypeId.ROADMAP
		});
	
		var infowindow = new google.maps.InfoWindow();
	
		var marker, i;
	
		for (i = 0; i < locations.length; i++) {  
	  
			marker = new google.maps.Marker({ 
			position: new google.maps.LatLng(locations[i][1], locations[i][2]), 
			map: map ,
			icon: 'images/marker1.png'
			});
	
	
		  google.maps.event.addListener(marker, 'click', (function(marker, i) {
			return function() {
			  infowindow.setContent(locations[i][0]);
			  infowindow.open(map, marker);
			}
		  })(marker, i));
		}
	})(jQuery);
  </script>
    
